import { MapContainer, TileLayer, Polyline, Marker, Popup, CircleMarker } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import { useState, useEffect } from 'react';
import axios from 'axios';
import L from 'leaflet';
import markerIcon2x from 'leaflet/dist/images/marker-icon-2x.png';
import markerIcon from 'leaflet/dist/images/marker-icon.png';
import markerShadow from 'leaflet/dist/images/marker-shadow.png';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  TimeScale,
  LinearScale,
  PointElement,
  LineElement,
  Tooltip,
  Legend,
} from 'chart.js';
import 'chartjs-adapter-date-fns';

ChartJS.register(TimeScale, LinearScale, PointElement, LineElement, Tooltip, Legend);

delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: markerIcon2x,
  iconUrl: markerIcon,
  shadowUrl: markerShadow,
});

export default function App() {
  const [positions, setPositions] = useState([]);
  const [uniqueMonths, setUniqueMonths] = useState([]);
  const [currentMonth, setCurrentMonth] = useState(null);
  const [selected, setSelected] = useState(null);
  const [timeSeries, setTimeSeries] = useState([]);
  const [highlightTime, setHighlightTime] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:5000/api/positions')
      .then(response => {
        const data = response.data;
        const months = [...new Set(data.map(p => p.obsTime.slice(6,9)))]; // MM-YYYY
        setUniqueMonths(months);
        if (months.length > 0) setCurrentMonth(months[0]);
      })
      .catch(error => console.error('Error fetching months:', error));
  }, []);

  useEffect(() => {
    if (!currentMonth) return;
    axios.get(`http://localhost:5000/api/positions?month=${currentMonth}`)
      .then(response => {
        setPositions(response.data);
      })
      .catch(error => console.error('Error fetching month positions:', error));
  }, [currentMonth]);

  const handleMarkerClick = (pos) => {
    setSelected(pos);
    setHighlightTime(pos.obsTime);

    axios.get(`http://localhost:5000/api/timeseries`)
      .then(res => {
        const allSeries = res.data;

        const clickedTime = new Date(pos.obsTime);
        const rangeStart = new Date(clickedTime);
        rangeStart.setDate(rangeStart.getDate() - 3);
        const rangeEnd = new Date(clickedTime);
        rangeEnd.setDate(rangeEnd.getDate() + 3);

        const nearby = allSeries.filter(p => {
          const time = new Date(p.time);
          return time >= rangeStart && time <= rangeEnd;
        });

        setTimeSeries(nearby);
      })
      .catch(err => console.error('Error fetching timeseries:', err));
  };

  const polyline = positions.map(p => [p.lat, p.lon]);
  const start = positions[0];
  const end = positions[positions.length - 1];
  const middle = positions.slice(1, -1);

  const chartData = {
  datasets: [
    {
      label: 'Barometric Pressure',
      data: timeSeries.map(p => ({
        x: new Date(p.time),
        y: p.pressure,
      })),
      borderColor: '#00BFFF', // DeepSkyBlue
      backgroundColor: '#1E90FF', // DodgerBlue
      pointRadius: 2,
      pointHoverRadius: 5,
      fill: false,
      tension: 0.4,
    },
    {
      label: 'Selected Time',
      data: highlightTime ? [{
        x: new Date(highlightTime),
        y: timeSeries.find(p => p.time === highlightTime)?.pressure || null,
      }] : [],
      pointBackgroundColor: 'red',
      pointBorderColor: 'red',
      pointRadius: 5,
      showLine: true,
    }
  ]
};

const chartOptions = {
  responsive: true,
  maintainAspectRatio: false,
  scales: {
    x: {
      type: 'time',
      time: {
        unit: 'day',
        tooltipFormat: 'dd-MM-yyyy HH:mm',
        displayFormats: {
          day: 'yyyy-MM-dd',
          hour: 'MMM d, HH:mm'
        }
      },
      grid: {
        color: '#ccc',
      },
      title: {
        display: true,
        text: 'Observation Time'
      }
    },
    y: {
      grid: {
        color: '#ccc',
      },
      title: {
        display: true,
        text: 'Barometric Pressure (hPa)'
      }
    }
  },
  plugins: {
    legend: {
      position: 'top',
      labels: {
        boxWidth: 12,
        boxHeight: 8,
      }
    },
    tooltip: {
      mode: 'nearest',
      intersect: false,
    }
  },
  elements: {
    point: {
      borderWidth: 1,
    }
  }
};

  return (
    <div style={{ display: 'flex', gap: '2rem', padding: '2rem' }}>
      <div style={{ flex: 1 }}>
        <select value={currentMonth} onChange={(e) => setCurrentMonth(e.target.value)}>
          {uniqueMonths.map((m, i) => (
            <option key={i} value={m}>{m}</option>
          ))}
        </select>

        <MapContainer center={[8.74, 69.15]} zoom={5} style={{ height: '600px', borderRadius: '1rem', marginTop: '1rem' }}>
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution="&copy; OpenStreetMap contributors"
          />
          <Polyline positions={polyline} color="red" />
          {start && (
            <Marker position={[start.lat, start.lon]} icon={L.icon({ iconUrl: 'https://maps.google.com/mapfiles/ms/icons/green-dot.png', iconSize: [25, 41], iconAnchor: [12, 41] })} eventHandlers={{ click: () => handleMarkerClick(start) }}>
              <Popup>Start: {start.fixTime}</Popup>
            </Marker>
          )}
          {end && start !== end && (
            <Marker position={[end.lat, end.lon]} icon={L.icon({ iconUrl: 'https://maps.google.com/mapfiles/ms/icons/blue-dot.png', iconSize: [25, 41], iconAnchor: [12, 41] })} eventHandlers={{ click: () => handleMarkerClick(end) }}>
              <Popup>End: {end.fixTime}</Popup>
            </Marker>
          )}
          {middle.map((pos, i) => (
            <CircleMarker key={i} center={[pos.lat, pos.lon]} pathOptions={{ color: 'yellow' }} radius={6} eventHandlers={{ click: () => handleMarkerClick(pos) }} />
          ))}
        </MapContainer>
      </div>

      <div style={{ flex: 1 }}>
        {selected ? (
          <div>
            <h2>Selected Point</h2>
            <p><strong>Latitude:</strong> {selected.lat}</p>
            <p><strong>Longitude:</strong> {selected.lon}</p>
            <p><strong>Fix Time:</strong> {selected.fixTime}</p>
            <p><strong>Obs Time:</strong> {selected.obsTime}</p>
            <div style={{ marginTop: '2rem' }}>
              <h3>Barometric Pressure (±3 days)</h3>
              <div style={{ height: '400px' }}>
                      <Line data={chartData} options={chartOptions} />
              </div>
            </div>
          </div>
        ) : (
          <p>Select a marker on the map to view pressure data.</p>
        )}
      </div>
    </div>
  );
}
