import { MapContainer, TileLayer, Polyline, Marker, Popup,CircleMarker } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import { useState, useEffect } from 'react';
import axios from 'axios';
import L from 'leaflet';
import markerIcon2x from 'leaflet/dist/images/marker-icon-2x.png';
import markerIcon from 'leaflet/dist/images/marker-icon.png';
import markerShadow from 'leaflet/dist/images/marker-shadow.png';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, TimeScale, LineElement, PointElement, LinearScale, Title, Tooltip, Legend } from 'chart.js';
import 'chartjs-adapter-date-fns';

ChartJS.register(TimeScale, LineElement, PointElement, LinearScale, Title, Tooltip, Legend);


delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: markerIcon2x,
  iconUrl: markerIcon,
  shadowUrl: markerShadow,
});

export default function App() {
  const [positions, setPositions] = useState([]);
  const [uniqueDays, setUniqueDays] = useState([]);
  const [currentDay, setCurrentDay] = useState(null);
  const [selected, setSelected] = useState(null);

  // Get all days initially
  useEffect(() => {
    axios.get('http://localhost:5000/api/positions')
      .then(response => {
        const data = response.data;
        const days = [...new Set(data.map(p => p.fixTime.split(' ')[0]))];
        setUniqueDays(days);
        if (days.length > 0) {
          setCurrentDay(days[0]);
        }
      })
      .catch(error => console.error('Error fetching days:', error));
  }, []);

  // Fetch only for selected day
  useEffect(() => {
    if (!currentDay) return;
    axios.get(`http://localhost:5000/api/positions?date=${currentDay}`)
      .then(response => {
        setPositions(response.data);
      })
      .catch(error => console.error('Error fetching day positions:', error));
  }, [currentDay]);

  const handleMarkerClick = (pos) => {
    setSelected(pos);
    axios.get(`http://localhost:5000/api/timeseries?lat=${pos.lat}&lon=${pos.lon}`)
      .then(res => setTimeSeries(res.data))
      .catch(err => console.error(err));
  };

  const polyline = positions.map(p => [p.lat, p.lon]);

  const chartData = {
    labels: timeSeries.map(p => p.fixTime),
    datasets: [
      {
        label: 'Î”Time (min)',
        data: timeSeries.map(p => (new Date(p.fixTime) - new Date(p.obsTime)) / 60000),
        borderColor: 'blue',
        tension: 0.4,
      }
    ]
  };


  //const polyline = positions.map(p => [p.lat, p.lon]);

  return (
    <div style={{ display: 'flex', gap: '2rem', padding: '2rem' }}>
      <div style={{ flex: 1 }}>
        <div style={{ marginBottom: '1rem' }}>
          <label>Select Day: </label>
          <select value={currentDay} onChange={(e) => setCurrentDay(e.target.value)}>
            {uniqueDays.map((day, index) => (
              <option key={index} value={day}>{day}</option>
            ))}
          </select>
        </div>

        <MapContainer center={[8.74, 69.15]} zoom={5} scrollWheelZoom={true} style={{ height: '600px', borderRadius: '1rem' }}>
          <TileLayer
            url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
            attribution="&copy; OpenStreetMap contributors"
          />
          <Polyline positions={polyline} color="red" />
          {positions.length > 0 && (
          <Marker position={[positions[0].lat, positions[0].lon]} icon={L.divIcon({ className: 'start-marker', html: '<div style="background-color: green; width: 12px; height: 12px; border-radius: 50%;"></div>' })} />
        )}

        {positions.length > 1 && (
          <Marker position={[positions[positions.length - 1].lat, positions[positions.length - 1].lon]} icon={L.divIcon({ className: 'end-marker', html: '<div style="background-color: blue; width: 12px; height: 12px; border-radius: 50%;"></div>' })} />
        )}

        {positions.slice(1, -1).map((pos, index) => (
          <CircleMarker
            key={index}
            center={[pos.lat, pos.lon]}
            radius={3}
            color="gray"
            eventHandlers={{ click: () => handleMarkerClick(pos) }}
          >
            <Popup>
              <div>
                <strong>Fix Time:</strong> {pos.fixTime}<br />
                <strong>Obs Time:</strong> {pos.obsTime}<br />
                <strong>Î”Time:</strong> {Math.round((new Date(pos.fixTime) - new Date(pos.obsTime)) / 60000)} min
              </div>
            </Popup>
          </CircleMarker>
        ))}
        </MapContainer>
      </div>

      <div style={{ flex: 2 }}>
        {selected ? (
          <div>
            <h2>Selected Point</h2>
            <p><strong>Latitude:</strong> {selected.lat}</p>
            <p><strong>Longitude:</strong> {selected.lon}</p>
            <p><strong>Position Fix Time:</strong> {selected.fixTime}</p>
            <p><strong>Observation Time:</strong> {selected.obsTime}</p>
            <p><strong>Time Difference:</strong> {Math.round((new Date(selected.fixTime) - new Date(selected.obsTime)) / 60000)} minutes</p>
            {<div>
              <h3>Time Series Plot for Selected Marker</h3>
              <Line data={chartData} options={{ scales: { x: { type: 'time', time: { unit: 'minute' } } }}} />
            </div>}
          </div>
        ) : (
          <p>Select a marker on the map to view details.</p>
        )}
      </div>

    </div>
  );
}
