import { MapContainer, TileLayer, Polyline, Marker, Popup, CircleMarker } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import { useState, useEffect } from 'react';
import axios from 'axios';
import L from 'leaflet';
import markerIcon2x from 'leaflet/dist/images/marker-icon-2x.png';
import markerIcon from 'leaflet/dist/images/marker-icon.png';
import markerShadow from 'leaflet/dist/images/marker-shadow.png';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  TimeScale,
  LinearScale,
  PointElement,
  LineElement,
  Tooltip,
  Legend,
} from 'chart.js';
import 'chartjs-adapter-date-fns';

ChartJS.register(TimeScale, LinearScale, PointElement, LineElement, Tooltip, Legend);

delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: markerIcon2x,
  iconUrl: markerIcon,
  shadowUrl: markerShadow,
});

export default function App() {
  const [positions, setPositions] = useState([]);
  const [uniqueMonths, setUniqueMonths] = useState([]);
  const [currentMonth, setCurrentMonth] = useState(null);
  const [selected, setSelected] = useState(null);
  const [seriesData, setSeriesData] = useState([]);
  const [highlightTime, setHighlightTime] = useState(null);

  useEffect(() => {
    axios.get('http://localhost:5000/api/positions')
      .then(response => {
        const data = response.data;
        const months = [...new Set(data.map(p => p.obsTime.slice(3, 10)))]; // Extract MM-YYYY
        setUniqueMonths(months);
        if (months.length > 0) setCurrentMonth(months[0]);
      });
  }, []);

  useEffect(() => {
    if (!currentMonth) return;
    axios.get(`http://localhost:5000/api/positions?month=${currentMonth}`)
      .then(response => setPositions(response.data));
  }, [currentMonth]);

  const handleMarkerClick = (pos) => {
    setSelected(pos);
    setHighlightTime(pos.obsTime);

    axios.get('http://localhost:5000/api/timeseries')
      .then(res => {
        const all = res.data;

        const clickedTime = new Date(pos.obsTime);
        const rangeStart = new Date(clickedTime);
        rangeStart.setDate(rangeStart.getDate() - 3);
        const rangeEnd = new Date(clickedTime);
        rangeEnd.setDate(rangeEnd.getDate() + 3);

        const filtered = all.filter(p => {
          const t = new Date(p.time);
          return t >= rangeStart && t <= rangeEnd;
        });

        setSeriesData(filtered);
      });
  };

  const createChart = (label, key, color) => {
    const data = {
      datasets: [
        {
          label,
          data: seriesData
            .filter(p => p[key] !== null)
            .map(p => ({
              x: new Date(p.time),
              y: p[key]
            })),
          borderColor: color,
          backgroundColor: color,
          pointRadius: 2,
          pointHoverRadius: 5,
          tension: 0.3,
        },
        {
          label: 'Selected Point',
          data: highlightTime ? [{
            x: new Date(highlightTime),
            y: seriesData.find(p => p.time === highlightTime)?.[key] || null,
          }] : [],
          pointBackgroundColor: 'red',
          pointBorderColor: 'red',
          pointRadius: 5,
          showLine: false,
        }
      ]
    };

    const options = {
      responsive: true,
      maintainAspectRatio: false,
      scales: {
        x: {
          type: 'time',
          time: {
            unit: 'day',
            tooltipFormat: 'dd-MM-yyyy HH:mm',
            displayFormats: {
              day: 'dd-MM-yyyy'
            }
          },
          title: {
            display: true,
            text: 'Observation Time'
          }
        },
        y: {
          title: {
            display: true,
            text: label
          }
        }
      },
      plugins: {
        legend: {
          position: 'top'
        }
      }
    };

    return (
      <div style={{ marginBottom: '2rem', height: '300px' }}>
        <Line data={data} options={options} />
      </div>
    );
  };

  const polyline = positions.map(p => [p.lat, p.lon]);
  const start = positions[0];
  const end = positions[positions.length - 1];
  const middle = positions.slice(1, -1);

  return (
    <div style={{ display: 'flex', gap: '2rem', padding: '2rem' }}>
      <div style={{ flex: 1 }}>
        <select value={currentMonth} onChange={(e) => setCurrentMonth(e.target.value)}>
          {uniqueMonths.map((m, i) => <option key={i} value={m}>{m}</option>)}
        </select>

        <MapContainer center={[8.74, 69.15]} zoom={5} style={{ height: '600px', marginTop: '1rem' }}>
          <TileLayer url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png" />
          <Polyline positions={polyline} color="red" />
          {start && (
            <Marker position={[start.lat, start.lon]} icon={L.icon({ iconUrl: 'https://maps.google.com/mapfiles/ms/icons/green-dot.png' })} eventHandlers={{ click: () => handleMarkerClick(start) }}>
              <Popup>Start: {start.fixTime}</Popup>
            </Marker>
          )}
          {end && (
            <Marker position={[end.lat, end.lon]} icon={L.icon({ iconUrl: 'https://maps.google.com/mapfiles/ms/icons/blue-dot.png' })} eventHandlers={{ click: () => handleMarkerClick(end) }}>
              <Popup>End: {end.fixTime}</Popup>
            </Marker>
          )}
          {middle.map((pos, i) => (
            <CircleMarker key={i} center={[pos.lat, pos.lon]} pathOptions={{ color: 'yellow' }} radius={5} eventHandlers={{ click: () => handleMarkerClick(pos) }} />
          ))}
        </MapContainer>
      </div>

      <div style={{ flex: 1 }}>
        {selected ? (
          <div>
            <h3>Details</h3>
            <p><strong>Lat:</strong> {selected.lat}</p>
            <p><strong>Lon:</strong> {selected.lon}</p>
            <p><strong>Obs Time:</strong> {selected.obsTime}</p>
            {createChart('Barometric Pressure (hPa)', 'pressure', '#0077b6')}
            {createChart('Sea Surface Temperature (°C)', 'seaSurfaceTemperature', '#ff8800')}
            {createChart('Submergence (%)', 'submergence', '#6a4c93')}
          </div>
        ) : (
          <p>Select a marker to view data.</p>
        )}
      </div>
    </div>
  );
}
